<?php /**  * Plugin Name: WTP MU Fatal Catcher  * Description: Loguje błędy PHP (w tym fatal) do wp-content/uploads/wtp-logs/wp-debug-latest.txt  * Author: WTP  * Version: 1.0  */  if (!defined('WP_CONTENT_DIR')) { return; }  @ini_set('log_errors', '1');  $dir = WP_CONTENT_DIR . '/uploads/wtp-logs'; if (!is_dir($dir)) { @mkdir($dir, 0755, true); } $log = $dir . '/wp-debug-latest.txt'; @ini_set('error_log', $log);  register_shutdown_function(function () {     $e = error_get_last();     if ($e && in_array($e['type'], [E_ERROR, E_PARSE, E_COMPILE_ERROR, E_CORE_ERROR])) {         $msg = sprintf('FATAL: %s in %s:%d', $e['message'], $e['file'], $e['line']);         error_log($msg);     } });