				}
			);
			$( '.cli_sub_tab' ).each(
				function(){
					var elm = $( this ).children( 'li' ).eq( 0 );
					elm.click();
				}
			);
			$( '#cli_non-ncessary_form' ).submit(
				function(e){
					e.preventDefault();
					var data       = $( this ).serialize();
