    container.setAttribute("aria-label", "We value your privacy");
    container.setAttribute("role", "region");

    const type = _ckyStore._bannerConfig.settings.type;
    let position = _ckyStore._bannerConfig.settings.position;
    let bannerType = type;
    if (bannerType === 'popup') {
