/* Webtoffe GDPR Cookie Consent Tooltip */

/* Add this attribute to the element that needs a tooltip */

[data-wt-cli-tooltip] {
	position: relative;
	z-index: 9999;
	cursor: pointer;
}

/* Hide the tooltip content by default */

[data-wt-cli-tooltip]:before, [data-wt-cli-tooltip]:after {
	visibility: hidden;
	opacity: 0;
	pointer-events: none;
}

/* Position tooltip above the element */

[data-wt-cli-tooltip]:before {
	position: absolute;
	bottom: 100%;
	left: 50%;
	margin-bottom: 10px;
	transform: translateX(-50%);
	min-width: 250px;
	-webkit-border-radius: 4px;
	-moz-border-radius: 4px;
	border-radius: 4px;
	background-color: #333;
	color: #fff;
	content: attr(data-wt-cli-tooltip);
	text-align: center;
	font-size: 12px;
	line-height: 18px;
	font-weight: normal;
	padding: 12px;
	letter-spacing: -.25px;
	text-transform: none;
	white-space: normal;
}

/* Triangle hack to make tooltip look like a speech bubble */

[data-wt-cli-tooltip]:after {
	position: absolute;
	bottom: 100%;
	left: 50%;
	margin-bottom: 5px;
	margin-left: -5px;
	width: 0;
	border-top: 5px solid #000;
	border-top: 5px solid hsla(0, 0%, 20%, 0.9);
	border-right: 5px solid transparent;
	border-left: 5px solid transparent;
	content: " ";
	font-size: 0;
	line-height: 0;
}

/* Show tooltip content on hover */

[data-wt-cli-tooltip]:hover:before, [data-wt-cli-tooltip]:hover:after {
