<?php
if (!defined('ABSPATH')) exit;

class WTP_ROSNAP_Snapshot {
    public static function run() {
        $u = WTP_ROSNAP_Helpers::upload_dir();
        $root = WTP_ROSNAP_Helpers::ensure_upload_dir();
        $baseurl = trailingslashit($u['baseurl']) . 'wtp-snapshot/';
        $basedir = trailingslashit($u['basedir']) . 'wtp-snapshot/';

        // 1) Theme manifest + source copies
        $theme_dir = get_stylesheet_directory();
        $theme_uri = get_stylesheet_directory_uri();
        $theme_slug = basename($theme_dir);
        $theme_items = [];
        $max_copy = 512 * 1024; // 512 KB

        if (is_dir($theme_dir)) {
            $it = new RecursiveIteratorIterator(new RecursiveDirectoryIterator($theme_dir, FilesystemIterator::SKIP_DOTS));
            foreach ($it as $f) {
                /** @var SplFileInfo $f */
                if (!$f->isFile()) continue;
                $abs = $f->getPathname();
                $rel = WTP_ROSNAP_Helpers::rel_path($theme_dir, $abs);
                $ext = WTP_ROSNAP_Helpers::safe_ext($rel);
                $size = $f->getSize();
                $mtime = $f->getMTime();
                $md5 = md5_file($abs);
                $theme_items[] = [
                    'rel' => $rel,
                    'ext' => $ext,
                    'size' => $size,
                    'mtime' => $mtime,
                    'md5' => $md5,
                ];

                // Copy selected source types
                if (in_array($ext, ['php','js','css','scss','json']) && $size <= $max_copy) {
                    $dst = $basedir . 'theme/src/' . $rel;
                    $dstDir = dirname($dst);
                    if (!is_dir($dstDir)) wp_mkdir_p($dstDir);
                    @copy($abs, $dst);
                }
            }
            // write manifest
            WTP_ROSNAP_Helpers::write_json($basedir . 'theme/manifest.json', [
                'theme' => $theme_slug,
                'count' => count($theme_items),
                'items' => $theme_items,
            ]);
        }

        // 2) Plugins list (active)
        $active = get_option('active_plugins', []);
        $plugins = [];
        if (is_array($active)) {
            foreach ($active as $file) {
                $data = get_plugin_data(WP_PLUGIN_DIR . '/' . $file, false, false);
                $slug = dirname($file);
                $plugins[] = [
                    'file' => $file,
                    'slug' => $slug,
                    'name' => isset($data['Name']) ? $data['Name'] : $slug,
                    'version' => isset($data['Version']) ? $data['Version'] : '',
                    'is_custom' => (strpos($slug, 'wtp-') === 0),
                ];
            }
        }
        WTP_ROSNAP_Helpers::write_json($basedir . 'plugins.json', $plugins);

        // 3) Options subset
        $opt_keys = [
            'blogname','siteurl','home','timezone_string','WPLANG','active_plugins','permalink_structure'
        ];
        $opts = [];
        foreach ($opt_keys as $k) {
            $opts[$k] = get_option($k);
        }
        WTP_ROSNAP_Helpers::write_json($basedir . 'options.json', $opts);

        // 4) Taxonomies and terms (category focus)
        $taxes = get_taxonomies([], 'objects');
        $tx = [];
        foreach ($taxes as $key => $obj) {
            $tx[] = [
                'name' => $obj->name,
                'label' => $obj->label,
                'hierarchical' => $obj->hierarchical,
                'public' => $obj->public,
            ];
        }
        WTP_ROSNAP_Helpers::write_json($basedir . 'taxonomies.json', $tx);

        // terms: category
        $terms_payload = [];
        if (taxonomy_exists('category')) {
            $terms = get_terms([
                'taxonomy' => 'category',
                'hide_empty' => false,
            ]);
            if (!is_wp_error($terms)) {
                foreach ($terms as $t) {
                    $terms_payload[] = [
                        'term_id' => (int)$t->term_id,
                        'slug' => $t->slug,
                        'name' => $t->name,
                        'parent' => (int)$t->parent,
                        'count' => (int)$t->count,
                    ];
                }
            }
        }
        WTP_ROSNAP_Helpers::write_json($basedir . 'terms-category.json', $terms_payload);

        // 5) Menus (locations + items)
        $menus_out = [];
        $locs = get_nav_menu_locations();
        if (is_array($locs)) {
            foreach ($locs as $loc => $menu_id) {
                $items = wp_get_nav_menu_items($menu_id);
                $arr = [];
                if (is_array($items)) {
                    foreach ($items as $it) {
                        $arr[] = [
                            'ID' => (int)$it->ID,
                            'title' => $it->title,
                            'url' => $it->url,
                            'menu_item_parent' => (int)$it->menu_item_parent,
                            'object' => $it->object,
                            'type' => $it->type,
                        ];
                    }
                }
                $menus_out[] = [
                    'location' => $loc,
                    'menu_id' => (int)$menu_id,
                    'items' => $arr,
                ];
            }
        }
        WTP_ROSNAP_Helpers::write_json($basedir . 'menus.json', $menus_out);

        // 6) Index summary
        $summary = [
            'generated_at' => date('c'),
            'site' => home_url('/'),
            'theme' => $theme_slug,
            'uploads_url' => $baseurl,
            'files' => [
                'index' => $baseurl . 'index.json',
                'theme_manifest' => $baseurl . 'theme/manifest.json',
                'plugins' => $baseurl . 'plugins.json',
                'options' => $baseurl . 'options.json',
                'taxonomies' => $baseurl . 'taxonomies.json',
                'terms_category' => $baseurl . 'terms-category.json',
                'menus' => $baseurl . 'menus.json',
            ],
        ];
        WTP_ROSNAP_Helpers::write_json($basedir . 'index.json', $summary);

        return $summary;
    }
}

