<?php
if (!defined('ABSPATH')) exit;

class WTP_ROSNAP_Helpers {
    public static function upload_dir() {
        $u = wp_upload_dir();
        return $u;
    }

    public static function ensure_upload_dir() {
        $u = self::upload_dir();
        $dir = trailingslashit($u['basedir']) . 'wtp-snapshot';
        if (!is_dir($dir)) wp_mkdir_p($dir);
        // Podkatalogi
        @wp_mkdir_p($dir . '/theme');
        @wp_mkdir_p($dir . '/theme/src');
        // .htaccess nie dodajemy (wikszo hostingw i tak serwuje uploady)
        return $dir;
    }

    public static function write_json($path, $data) {
        $json = wp_json_encode($data, JSON_PRETTY_PRINT | JSON_UNESCAPED_SLASHES | JSON_UNESCAPED_UNICODE);
        return file_put_contents($path, $json);
    }

    public static function rel_path($base, $full) {
        $base = wp_normalize_path(trailingslashit($base));
        $full = wp_normalize_path($full);
        if (strpos($full, $base) === 0) return ltrim(substr($full, strlen($base)), '/');
        return $full;
    }

    public static function safe_ext($filename) {
        $ext = strtolower(pathinfo($filename, PATHINFO_EXTENSION));
        return $ext;
    }
}

