import {useState, useEffect} from "react";
import apiFetch from '@wordpress/api-fetch';
import { useSelect, select } from '@wordpress/data';

import ServerSideRender from "@wordpress/server-side-render";
import {useBlockProps, InspectorControls} from "@wordpress/block-editor";
import {
	PanelBody, SelectControl,
	TextControl,
	ToggleControl,
} from "@wordpress/components";
import {__} from '@wordpress/i18n';
import Connect from "./Components/Connect";
import Dialog from "./Components/Dialog";

const Edit = ({attributes, setAttributes, clientId}) => {
	const { isPostPublished, postPermalink } = useSelect((select) => ({
		isPostPublished: select('core/editor').isCurrentPostPublished()
	}));

	const blockProps = useBlockProps();
	const nonce = wp.data.select('core/editor').getEditorSettings().nonce || '';
	const [showNewContactList, setShowNewContactList] = useState(attributes.contactList === '');
	const [isConnected, setIsConnected] = useState(true);
	const [contactLists, setContactLists] = useState([]);
	const [showDialog, setShowDialog] = useState(false);
	const [lastStatus, setLastStatus] = useState(null);

	const layoutOptions = [
		{ label: __('Default', 'hostinger-reach'), value: 'default' },
		{ label: __('Inline', 'hostinger-reach'), value: 'inline' }
	];

	useEffect(() => {
		if ( isPostPublished && lastStatus !== null && lastStatus !== 'publish' ) {
			setShowDialog(true);
		}
	}, [isPostPublished, lastStatus]);


	useEffect(() => {
		setLastPostStatus();
		fetchContactLists();
		checkConnection();
	}, []);


	useEffect(() => {
		if (attributes.formId) return;
		setAttributes({formId: clientId});
	}, [setAttributes]);

	const fetchContactLists = async () => {
		await getContactLists();
	};


	const checkConnection = async () => {
		try {
			const response = await apiFetch({
				path: '/hostinger-reach/v1/overview',
				method: 'GET',
				headers: {
					'X-WP-Nonce': nonce,
				},
				parse: false,
			});

			if (response.ok) {
				setIsConnected(true);
			} else {
				setIsConnected(false);
			}

		} catch (err) {
			setIsConnected(false);
		}
	}

	const setLastPostStatus = () => {
		const lastKnownStatus = select('core/editor').getEditedPostAttribute('status');
		setLastStatus(lastKnownStatus);
	}

	const getContactLists = async () => {
		const response = await apiFetch({
			path: '/hostinger-reach/v1/contact-lists',
			method: 'GET',
			headers: {
				'X-WP-Nonce': nonce,
			},
			parse: false,
		});

		if (response.ok) {
			setContactLists(await response.json());
		}
	}

	return <div {...blockProps}>
		<InspectorControls key="hostinger-reach-block-controls">
			<PanelBody title={__("Settings", "hostinger-reach")}>
				{!isConnected && <Connect/>}
				<TextControl
					disabled
					label={__('Form ID', 'hostinger-reach')}
					value={attributes.formId}
					help={__('Unique identifier for this form', 'hostinger-reach')}
				/>
				<SelectControl
					label={__('Contact List', 'hostinger-reach')}
					value={attributes.contactList}
					options={[
						{label: __("Create New List", "hostinger-reach"), value: ''},
						...contactLists.map(list => {
							return {label: list.name, value: list.name}
						})
					]}
					onChange={(value) => {
						setAttributes({contactList: value})
						if (!value) {
							setShowNewContactList(true);
						} else {
							setShowNewContactList(false);
						}
					}}
				/>
				{showNewContactList && <TextControl
					label={__('New Contact List', 'hostinger-reach')}
					value={attributes.contactList}
					onChange={(value) => {
						setAttributes({contactList: value})
					}}
					help={__('Name for the new Contact List', 'hostinger-reach')}
				/>}
				<ToggleControl
					label={__("Show Name Field?", "hostinger-reach")}
					key="hostinger-reach-block-show-name-field"
					checked={attributes.showName}
					onChange={(value) =>
						setAttributes({showName: value})
					}
				/>
				<ToggleControl
					label={__("Show Surname Field?", "hostinger-reach")}
					key="hostinger-reach-block-show-surname-field"
					checked={attributes.showSurname}
					onChange={(value) =>
						setAttributes({showSurname: value})
					}
				/>
			</PanelBody>
			<PanelBody title={__('Layout Settings', 'hostinger-reach')}>
				<SelectControl
					label={__('Layout', 'hostinger-reach')}
					value={attributes.layout}
					options={layoutOptions}
					onChange={(value) => setAttributes({ layout: value })}
				/>
			</PanelBody>

		</InspectorControls>
		{!isConnected && <Connect/>}
		<ServerSideRender
			key="hostinger-reach-server-side-renderer"
			block="hostinger-reach/subscription"
			attributes={attributes}
		/>
		{showDialog && <Dialog onClose={() => setShowDialog(false)} /> }
	</div>

}

export default Edit;

document.addEventListener('DOMContentLoaded', function() {
	const translations = hostinger_reach_subscription_block_data.translations;
	const formSelector = '.hostinger-reach-block-subscription-form';
	const forms = document.querySelectorAll(formSelector);
	forms.forEach(form => {
		const messageEl = form.querySelector('.reach-subscription-message');
		form.addEventListener('submit', function(e) {
			e.preventDefault();

			const submitBtn = form.querySelector('button[type="submit"]');
			const formData = new FormData(form);
			const data = {};

			formData.forEach((value, key) => {
				data[key] = value;
			});

			submitBtn.disabled = true;

			fetch(hostinger_reach_subscription_block_data.endpoint, {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
					'X-WP-Nonce': hostinger_reach_subscription_block_data.nonce,
				},
				body: JSON.stringify(data)
			})
				.then(response => {
					messageEl.style.display = 'block';
					form.reset();

					if ( response.ok ) {
						messageEl.textContent = translations.thanks;
						const submitBtn = form.querySelector('.hostinger-reach-block-submit');
						submitBtn.style.display = 'none';
					} else {
						throw new Error(response.statusText);
					}

				})
				.catch(error => {
					messageEl.textContent = translations.error;
					messageEl.style.display = 'block';
					submitBtn.disabled = false;
				});
		});
	});
});

import {registerBlockType} from '@wordpress/blocks';
import {__} from '@wordpress/i18n';
import Edit from './edit';
import './styles.scss'

registerBlockType('hostinger-reach/subscription',
	{
		name: "hostinger-reach/subscription",
		title: __("Reach Subscription Form", "hostinger-reach"),
		icon:
			<svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
				<path
					d="M13.925 7.35306L18.0634 3.21429L20.7856 5.9365L18.5036 8.21843H15.8008L15.7823 8.20957L10.0747 5.67124V1.5H13.925V7.35306Z"
					fill="#1D1E20"/>
				<path
					d="M5.49586 15.7815L8.21638 15.7895L13.9252 18.3287V22.4999H10.0749L10.0736 16.649L5.93656 20.7856L3.21436 18.0634L5.49586 15.7815Z"
					fill="#1D1E20"/>
				<path
					d="M22.5003 10.0757H18.3282L15.7827 15.7997V18.505L18.0638 20.7861L20.786 18.0635L16.6472 13.9251L22.5003 13.9243V10.0757Z"
					fill="#1D1E20"/>
				<path
					d="M5.9365 3.21436L8.21632 5.49418V8.20415L5.67208 13.9244H1.5V10.0758H7.35306L3.21429 5.93741L5.9365 3.21436Z"
					fill="#1D1E20"/>
			</svg>,
		category: "common",
		textdomain: "hostinger-reach",
		attributes: {
			formId: {
				type: 'string',
				default: '',
			},
			showName: {
				type: 'boolean',
				default: false,
			},
			showSurname: {
				type: 'boolean',
				default: false,
			},
			contactList: {
				type: 'string',
				default: '',
			},
			layout: {
				type: 'string',
				default: 'default',
				enum: ['default', 'inline']
			}
		},
		edit: Edit,
		save: () => {
			return null;
		}
	}
);

import {__} from '@wordpress/i18n';
import './Connect.scss'

const Connect = () => {
	return <div className="hostinger-reach-block-connect">
		<div className="hostinger-reach-block-connect__title">
			{__("You are not connected to Hostinger Reach", "hostinger-reach")}
		</div>
		<div className="hostinger-reach-block-connect__subtitle">
			{__("You are not connected to Hostinger Reach. To gain full access to this block, you need to connect your Hostinger Reach account.", "hostinger-reach")}
		</div>
		<div className="hostinger-reach-block-connect__button-wrap"><a
			target="_blank"
			href="/wp-admin/admin.php?page=hostinger-reach"
			className="hostinger-block-button hostinger-block-button--is-normal hostinger-block-button--is-primary">
			{__("Connect Now", "hostinger-reach")}
		</a></div>
	</div>
}

export default Connect;

wp.domReady(() => {
	whenEditorIsReady().then(() => {
		let block = wp.blocks.createBlock('hostinger-reach/subscription', {});
		setTimeout(() => {
			wp.data.dispatch('core/block-editor').insertBlocks(block);
		}, 0);

	});

	function whenEditorIsReady() {
		return new Promise((resolve) => {
			const unsubscribe = wp.data.subscribe(() => {
				if (wp.data.select('core/editor').isCleanNewPost() || wp.data.select('core/block-editor').getBlockCount() > 0) {
					unsubscribe()
					resolve()
				}
			})
		})
	}
});

