	return (
		<InspectorControls key={ 'inspector' }>
			<PanelBody title={ __( 'FAQ Options', 'rank-math' ) }>
				<SelectControl
					label={ __( 'List Style', 'rank-math' ) }
					value={ attributes.listStyle }
					options={ [
						{
							value: '',
