(function(){
  const $=(s)=>document.querySelector(s);
  function setStatus(m){$('#wtp-status').textContent=m||'';}
  async function callHealth(method='POST'){
    const opt={method,headers:{'X-WP-Nonce':WTPHealth.nonce}};
    if(method==='POST'){opt.headers['Content-Type']='application/json'; opt.body='{}';}
    const res=await fetch(WTPHealth.restHealth,opt);
    if(!res.ok){throw new Error('REST '+res.status+': '+await res.text());}
    return res.json();
  }
  async function getHistory(){
    const res=await fetch(WTPHealth.restHistory,{headers:{'X-WP-Nonce':WTPHealth.nonce}});
    if(!res.ok){throw new Error('REST '+res.status+': '+await res.text());}
    return res.json();
  }
  function row(k,v,isOk=true){const tr=document.createElement('tr');tr.innerHTML=`<th>${esc(k)}</th><td class="${isOk?'ok':'fail'}">${esc(v||'')}</td>`;return tr;}
  function render(res){
    const sum=$('#wtp-summary');sum.classList.remove('bad');sum.style.display='block';
    sum.textContent=`Diagnoza wykonana. Build: ${esc(res.build||'')} · run_id: ${esc(res.run_id)} · ${res.duration_ms||0} ms · risk ${res.risk_score}`;
    const tbodyC=$('#wtp-core');tbodyC.innerHTML='';(res.core||[]).forEach(c=>tbodyC.appendChild(row(c.name,c.note,c.ok)));
    const tbodyP=$('#wtp-plugins');tbodyP.innerHTML='';(res.plugins||[]).forEach(p=>tbodyP.appendChild(row(p.name,p.note,p.ok)));
    const tbodyL=$('#wtp-locks');tbodyL.innerHTML='';(res.locks||[]).forEach(l=>tbodyL.appendChild(row(l.name,l.note,l.ok)));
    const sys=$('#wtp-system');sys.innerHTML='';Object.keys(res.system||{}).forEach(k=>sys.appendChild(row(k,res.system[k],true)));
    const tbodyU=$('#wtp-urls');tbodyU.innerHTML='';(res.urls||[]).forEach(u=>{
      const tr=document.createElement('tr');tr.innerHTML=`<td><code>${esc(u.url)}</code></td><td class="${u.ok?'ok':'fail'}">${u.status}</td><td>${u.time_ms??''}</td><td>${esc(u.note||'')}</td>`;tbodyU.appendChild(tr);});
    if(res.url_note)$('#wtp-url-note').textContent=res.url_note;
    $('#wtp-sections').classList.remove('hidden');
    if(res.history){paintHistory(res.history);} else {loadHistory();}
  }
  function paintHistory(hist){
    const tbody=$('#wtp-history');tbody.innerHTML='';
    (hist||[]).forEach(h=>{
      const tr=document.createElement('tr');
      tr.innerHTML=`<td>${esc(h.time)}</td><td class="${h.ok?'ok':'fail'}">${h.ok?'OK':'Fail'}</td><td>${esc(h.risk)}</td><td>${esc(h.note||'')}</td>`;
      tbody.appendChild(tr);
    });
  }
  async function loadHistory(){
    try{paintHistory(await getHistory());}catch(e){console.error(e);}
  }
  function esc(s){return String(s||'').replace(/[&<>"']/g,m=>({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;'}[m]));}
  async function run(){setStatus('Uruchamiam…');try{const data=await callHealth('POST');render(data);setStatus('Gotowe.');}catch(e){alert(e.message||e);setStatus('Błąd.');}}
  async function downloadJson(){
    try{
      const data=await callHealth('GET');
      const blob=new Blob([JSON.stringify(data,null,2)],{type:'application/json;charset=utf-8'});
      const url=URL.createObjectURL(blob);
      const a=document.createElement('a');a.href=url;a.download='wtp-health-'+Date.now()+'.json';a.click();URL.revokeObjectURL(url);
      if(data.history){paintHistory(data.history);} else {loadHistory();}
    }catch(e){alert(e.message||e);}
  }
  document.addEventListener('DOMContentLoaded',()=>{
    $('#wtp-run').addEventListener('click',run);
    $('#wtp-json').addEventListener('click',downloadJson);
    loadHistory();
  });
})();

.wtp-health .panel{margin:10px 0;}
.wtp-health .build{font-size:12px;opacity:.7;margin-left:6px;}
.wtp-health .status{margin-left:10px;opacity:.8;}
.wtp-health .summary{margin-top:8px;padding:10px;border-left:4px solid #46b450;background:#f6fff5;display:none;}
.wtp-health .summary.bad{border-color:#b32d2e;background:#fff5f5;}
.wtp-health .sections.hidden{display:none;}
.wtp-health td.ok{color:#25821b;font-weight:600;}
.wtp-health td.fail{color:#b32d2e;font-weight:600;}
@media(max-width:782px){.wtp-health table.widefat td:nth-child(3){display:none;}}

(function ($) {
	'use strict';

	/**
	 * All of the code for your public-facing JavaScript source
	 * should reside in this file.
	 *
	 * Note: It has been assumed you will write jQuery code here, so the
	 * $ function reference has been prepared for usage within the scope
	 * of this function.
	 *
	 * This enables you to define handlers, for when the DOM is ready:
	 *
	 * $(function() {
	 *
	 * }) ;
	 *
	 * When the window is loaded:
	 *
	 * $( window ).load(function() {
	 *
	 * }) ;
	 *
	 * ...and/or other possibilities.
	 *
	 * Ideally, it is not considered best practise to attach more than a
	 * single DOM-ready or window-load handler for a particular page.
	 * Although scripts in the WordPress core, Plugins and Themes may be
	 * practising this, we should strive to set a better example in our own work.
	 */

	jQuery(document).ready(function () {
		/************** Common LiteSpeed JS **************/
		// Link confirm
		$('[data-litespeed-cfm]').on('click', function (event) {
			cfm_txt = $.trim($(this).data('litespeed-cfm')).replace(/\\n/g, '\n');
			if (cfm_txt === '') {
				return true;
			}
			if (confirm(cfm_txt)) {
				return true;
			}
			event.preventDefault();
			event.stopImmediatePropagation();
			return false;
		});

		/************** LSWCP JS ****************/
		// page tab switch functionality
		(function () {
			var hash = window.location.hash.substr(1);
			var $tabs = $('[data-litespeed-tab]');
			var $subtabs = $('[data-litespeed-subtab]');

			// Handle tab and subtab events
			var tab_action = function ($elems, type) {
				type = litespeed_tab_type(type);
				var data = 'litespeed-' + type;
				$elems.on('click', function (_event) {
					litespeed_display_tab($(this).data(data), type);
					document.cookie = 'litespeed_' + type + '=' + $(this).data(data);
					$(this).blur();
				});
			};
			tab_action($tabs);
			tab_action($subtabs, 'subtab');

			if (!$tabs.length > 0) {
				// No tabs exist
				return;
			}

			// Find hash in tabs and subtabs
			var $hash_tab = $tabs.filter('[data-litespeed-tab="' + hash + '"]:first');
			var $hash_subtab = $subtabs.filter('[data-litespeed-subtab="' + hash + '"]:first');

			// Find tab name
			var $subtab;
			var $tab;
			var tab_name;
			if ($hash_subtab.length > 0) {
				// Hash is a subtab
				$tab = $hash_subtab.closest('[data-litespeed-layout]');
				if ($tab.length > 0) {
					$subtab = $hash_subtab;
					tab_name = $tab.data('litespeed-layout');
				}
			}
			if (typeof $tab === 'undefined' || $tab.length < 1) {
				// Maybe hash is a tab
				$tab = $hash_tab;
				if ($tab.length < 1) {
					// Maybe tab cookie exists
					$tab = litespeed_tab_cookie($tabs);
					if ($tab.length < 1) {
						// Use the first tab by default
						$tab = $tabs.first();
					}
				}
				if (typeof tab_name === 'undefined') {
					tab_name = $tab.data('litespeed-tab');
				}
			}

			// Always display a tab
			litespeed_display_tab(tab_name);

			// Find subtab name
			if (typeof $subtab === 'undefined' || $subtab.length < 1) {
				$subtab = litespeed_tab_cookie($subtabs, 'subtab');
			}
			if ($subtab.length > 0) {
				var subtab_name = $subtab.data('litespeed-subtab');
				// Display a subtab
				litespeed_display_tab(subtab_name, 'subtab');
			}
		})();

		/******************** Clear whm msg ********************/
		$(document).on('click', '.lscwp-whm-notice .notice-dismiss', function () {
			$.get(litespeed_data.ajax_url_dismiss_whm);
		});
		/******************** Clear rule conflict msg ********************/
		$(document).on('click', '.lscwp-notice-ruleconflict .notice-dismiss', function () {
			$.get(litespeed_data.ajax_url_dismiss_ruleconflict);
		});

		/** Accesskey **/
		$('[litespeed-accesskey]').map(function () {
			var thiskey = $(this).attr('litespeed-accesskey');
			if (thiskey == '') {
				return;
			}
			$(this).attr('title', 'Shortcut : ' + thiskey.toLocaleUpperCase());
			var that = this;
			$(document).on('keydown', function (e) {
				if ($(':input:focus').length > 0) return;
				if (event.metaKey) return;
				if (event.ctrlKey) return;
				if (event.altKey) return;
				if (event.shiftKey) return;
				if (litespeed_keycode(thiskey.charCodeAt(0))) $(that)[0].click();
			});
		});

		/** Lets copy one more submit button **/
		if ($('input[name="LSCWP_CTRL"]').length > 0) {
			var btn = $('input.litespeed-duplicate-float');
			btn.clone().addClass('litespeed-float-submit').removeAttr('id').insertAfter(btn);
		}
		if ($('input[id="LSCWP_NONCE"]').length > 0) {
			$('input[id="LSCWP_NONCE"]').removeAttr('id');
		}

		/**
		 * Human readable time conversation
		 * @since  3.0
		 */
		if ($('[data-litespeed-readable]').length > 0) {
			$('[data-litespeed-readable]').each(function (index, el) {
				var that = this;
				var $input = $(this).siblings('input[type="text"]');

				var txt = litespeed_readable_time($input.val());
				$(that).html(txt ? '= ' + txt : '');

				$input.on('keyup', function (event) {
					var txt = litespeed_readable_time($(this).val());
					$(that).html(txt ? '= ' + txt : '');
				});
			});
		}

		/**
		 * Click only once
		 */
		if ($('[data-litespeed-onlyonce]').length > 0) {
			$('[data-litespeed-onlyonce]').on('click', function (e) {
				if ($(this).hasClass('disabled')) {
					e.preventDefault();
				}
				$(this).addClass('disabled');
			});
		}
	});
})(jQuery);

/**
 * Plural handler
 */
