theme/style.css:
/*
 Theme Name: WTP Core Theme
 Theme URI: https://weektoppick.com/
 Author: WTP
 Description: Minimal, mobile-first theme for WeekTopPick (language switcher with flags, category chips with subchips on click, charity banner, Accept-Language prompt, and basic pages).
 Version: 1.6.3
 License: MIT
 Text Domain: wtp
*/
:root{
  --bg:#ffffff;
  --fg:#111827;
  --muted:#6b7280;
  --chip-bg:#f3f4f6;
  --chip-border:#e5e7eb;
  --chip-active:#111827;
  --chip-active-bg:#e8edf9;
  --brand:#0b5cff;
  --banner:#169c46;
}
*{box-sizing:border-box;}
html,body{margin:0;padding:0;background:var(--bg);color:var(--fg);font:16px/1.5 -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica,Arial,sans-serif;}
a{color:var(--brand);text-decoration:none}
a:hover{text-decoration:underline}
.container{max-width:1080px;margin:0 auto;padding:16px}
h1,h2,h3{margin:16px 0 8px}
/* Language bar */
.wtp-langbar{display:flex;gap:8px;overflow:auto;padding:8px 0;margin-bottom:8px}
.wtp-langbar a{display:flex;align-items:center;gap:6px;border:1px solid var(--chip-border);background:var(--chip-bg);padding:8px 12px;border-radius:999px;white-space:nowrap}
.wtp-langbar a.active{background:var(--chip-active-bg);border-color:#c7d2fe;color:var(--chip-active)}
/* Chips */
.wtp-chips{display:flex;gap:12px;overflow:auto;padding:8px 0;margin-bottom:12px}
.wtp-chip{display:flex;align-items:center;gap:8px;border:1px solid var(--chip-border);background:var(--bg);padding:10px 14px;border-radius:999px;white-space:nowrap}
.wtp-chip:hover{box-shadow:0 1px 4px rgba(0,0,0,.06)}
/* Subchips (revealed after click) */
.wtp-subchips{display:none;gap:8px;flex-wrap:wrap;padding:8px 0;margin-bottom:12px}
.wtp-subchips.show{display:flex}
.wtp-subchip{border:1px dashed var(--chip-border);padding:8px 12px;border-radius:999px;white-space:nowrap}
/* Charity banner */
.wtp-charity{background:var(--banner);color:#fff;border-radius:8px;padding:14px 16px;margin:16px 0;display:flex;flex-wrap:wrap;align-items:center;gap:10px}
.wtp-charity a{color:#fff;text-decoration:underline}
.wtp-charity .select{margin-left:auto;background:#fff;color:#111;border-radius:6px;padding:4px 8px;border:none}
/* Cards */
.card{background:#f8fafc;border:1px solid #e5e7eb;border-radius:16px;margin:12px 0;overflow:hidden;box-shadow:0 1px 6px rgba(0,0,0,.05)}
.card .ph{background:#e5e7eb;height:180px;display:flex;align-items:center;justify-content:center;color:#6b7280}
.card .body{padding:16px}
.btn{display:inline-block;background:var(--brand);color:#fff;padding:12px 16px;border-radius:12px;margin-top:8px}
/* Prompt */
.wtp-prompt{position:fixed;left:12px;right:12px;bottom:12px;background:#111827;color:#fff;border-radius:12px;padding:12px 16px;display:none;align-items:center;gap:10px;z-index:9999}
.wtp-prompt.show{display:flex}
.wtp-prompt button{border:0;border-radius:10px;padding:8px 12px}
.wtp-prompt .stay{background:#374151;color:#fff}
.wtp-prompt .switch{background:var(--brand);color:#fff}
/* Footer build */
.build{color:var(--muted);font-size:12px;margin-top:12px}
