<?php /**  * WTP Core Theme – SAFE functions.php  * Minimal bootstrap to recover from previous fatal in functions.php  * - no BOM, no closing PHP tag, no output/echo  * - guarded hooks, zero side-effects before WP loads  */  if (!defined('ABSPATH')) {     exit; }  /**  * Theme supports – minimal set, safe to call.  */ add_action('after_setup_theme', function () {     add_theme_support('title-tag');     add_theme_support('html5', ['search-form', 'gallery', 'caption', 'style', 'script']);     // add_theme_support('post-thumbnails'); // włącz, jeśli potrzebne }, 0);  /**  * Early init – do nothing risky, no headers/cookies here.  */ add_action('init', function () {     // miejsce na bezpieczne rejestry (taxonomies, post types) — puste na razie }, 1);  /**  * Enqueue – pusta, żeby nie wywalać.  */ add_action('wp_enqueue_scripts', function () {     // w razie potrzeby dorzucimy CSS/JS w kolejnej iteracji }, 10);  /**  * Admin – pusta (nie dotykamy panelu, żeby się podniósł).  */ add_action('admin_init', function () {     // nic }, 10);